                <div class="col-md-10 col-lg-8 col-xl-7 mx-auto">
                    <form  id="contactForm" name="sentMessage" novalidate="novalidate">
                        <div style='display: block; margin: 0;' class="form-row">
                            <!-- <div class="col-3" style='display: none;'><div></div></div> -->
                            <div style='align-self: center;' class="col-12">
                                <p style='font-weight: 600;'>Name</p>
                                <div class="form-group">
                                    <input class="form-control" id="name" type="text" placeholder="" required="required" data-validation-required-message="Please enter your name." />
                                    <p class="help-block text-danger"></p>
                                </div>
                                <p style='font-weight: 600;'>Email</p>
                                <div class="form-group">
                                    <input class="form-control" id="email" type="email" placeholder="" required="required" data-validation-required-message="Please enter your email address." />
                                    <p class="help-block text-danger"></p>
                                </div>
                                <p style='font-weight: 600;'>Message</p>
                                <div class="form-group form-group-textarea">
                                    <textarea class="form-control" id="message" placeholder="" required="required" data-validation-required-message="Please enter a message."></textarea>
                                    <p class="help-block text-danger"></p>
                                </div>
                            </div>
                            <!-- <div class="col-3" style='display:none;'><div></div></div> -->
                            <!-- <div class="col-md-6">
                                
                            </div> -->
                        </div>
                        <div class="text-center">
                            <div id="success"></div>
                            <button style='padding: 0.75rem 2.5rem;' class="btn btn-primary btn-xl" id="sendMessageButton" type="submit">Send Message</button>
                        </div>
                    </form>
                </div>
            </div>
